-- next appointment field and additional option in swes
ALTER TABLE `opsz_aufnahme` ADD COLUMN `naechstertermin` DATETIME DEFAULT NULL;
INSERT  INTO `opsz_aufnahme_permissions` (`keymachine`,`keyreadable`,`realid`,`typelist`,`edittype`,`role_0`) SELECT 'naechstertermin','nächster Termin','17.9','DATETIME','DATETIME','0' FROM DUAL WHERE (SELECT count(*) FROM information_schema.COLUMNS WHERE TABLE_NAME = 'opsz_aufnahme' AND COLUMN_NAME = 'naechstertermin') > 0 AND (SELECT count(keymachine) FROM `opsz_aufnahme_permissions` where keymachine = 'naechstertermin') = 0;
UPDATE `opsz_aufnahme_references` SET `allowed_values`= '["","Hausarzt / Hausärztin","Psychiater*in / Neurolog*in","Psycholog*innen/Psychotherapeut*innen","Kliniken","Unterstützer*in","Mitarbeiter*innen der Flüchtlingsunterkunft","Angehörig*e","Rechtsanwalt / Rechtsanwältin","Ausländerbehörde","Sozialamt / Agentur für Arbeit / jobcenter","Krankenkasse","Beratungsstellen","Familienhilfe","gesetzliche*r Betreuer*in / Vormund","Bundesamt"]' WHERE `referencetag`= '_swes';
